import React from 'react';
import './App.css';
import {Query} from 'react-apollo';
import gql from 'graphql-tag';

export const SINGLE_UPLOAD = gql`
  mutation singleUpload($file: Upload) {
    singleUpload(group_id: $group_id, product_id: $product_id, product_option_id: $product_option_id, product_supplier_id: $product_supplier_id) {
      filename
      mimetype
      encoding
    }
  }
`


function App() {
  return (
    <div className="App">
      <Query query={GET_CURRENT_USER}>
        {({data, loading}) => {
          const { products } = data;
          if (loading || products.length <= 0) {
            return <div>Loading ...</div>;
          }
          return (
            <>{products[0].name}</>
          )
        }}
      </Query>
    </div>
  );
}

export default App;
