import React from 'react';
import './App.css';
import {Query} from 'react-apollo';
import gql from 'graphql-tag';

const GET_CURRENT_USER = gql`
  {
    viewer {
      login
      name
    }
  }
`;

function App() {
  return (
    <div className="App">
      <Query query={GET_CURRENT_USER}>
        {({data, loading}) => {
          const { viewer } = data;
          if (loading || !viewer) {
            return <div>Loading ...</div>;
          }
          return (
            <>{viewer.name} {viewer.login}</>
          )
        }}
      </Query>
    </div>
  );
}

export default App;
